import { useParams } from "react-router-dom";
import { getMovie } from "../../api/movies";
import { useQuery } from "react-query";
import { Spinner } from "reactstrap";
import { Button } from 'reactstrap';
import './Movie.css';

const Movie = () => {
  const { movieId } = useParams();
  const { status, data} = useQuery(
    ["movie", movieId],
    () => getMovie(movieId)
  );

 console.log(status);

  return  (

    <div>
      {status === "loading" ? (
      <Spinner>Loading...</Spinner>
      ) : (
      <div class="movie-container">
        <h1>Movie details</h1>
        <div class="movie-title">
          <h2 class="title">{data.title}</h2>
          <div class="edit">
            <Button color="danger" class="delete">Delete</Button>
            <Button color="primary">Edit</Button>
          </div>
        </div>
        <label>Tagline: </label><span>{data.tagline}</span>
        <div>
          <label>Stars: </label><span>{data.vote_average}</span>
          <label class="label">IMDB:</label><a href={data.homepage} target="_blank">IMDB Link</a>
        </div>
        <h4>Overview:</h4>
        <p>{data.overview}</p>
        <img src={data.poster_path}></img>
      </div>
      )}
    </div>
  );
};

export default Movie;
