import { getMovies } from "../../api/movies";
import { useQuery } from "react-query";
import { Card,Table, Spinner } from "reactstrap";
import { useNavigate } from "react-router-dom";



const Movies = () =>{

    const { status, data: movies } = useQuery("movies", getMovies);
    
    const navigate = useNavigate();
    const goToMoviePage = () => {
        navigate("/movies/:movieId");
      };
    //console.log(movies)


    return(
        <div>
        <h1>Movies</h1>
        {status === "loading" ? (
            <Spinner>Loading...</Spinner>
            ) :  (movies?.map( ( { backdrop_path, title, overview})=> (
                        <div class="card" style="width: 18rem;">
                            <img src={backdrop_path} class="card-img-top" alt="15rem;"></img>
                            <div class="card-body">
                                <img src={backdrop_path}></img>
                                <h5 class="card-title">{title}</h5>
                                <p class="card-text">{overview}</p>
                                <a href={goToMoviePage} class="btn btn-primary">Go somewhere</a>
                            </div>
                        </div>
                        )
                    )
                )
        }          
    </div>  
    )
} 

export default Movies;
