import { getMovies } from "../../api/movies";
import { useQuery } from "react-query";
import { Table, Spinner } from "reactstrap";


const Movies = () =>{

    const { status, data: movies } = useQuery("movies", getMovies);

    //console.log(movies)


    return(
        <div>
        <h1>Movies</h1>
        {status === "loading" ? (
            <Spinner>Loading...</Spinner>
            ) :  (movies?.map( ( { backdrop_path, title, overview})=> (
                        <div class="card">
                            <div class="card-body">
                                <img src={backdrop_path}></img>

                                <h3>{title}</h3>

                                <h3>{overview}</h3>  
                            </div>
                        </div>
                        )
                    )
                )
        }          
    </div>  
    )
} 

export default Movies;
