import { getMovies } from "../../api/movies";
import { useQuery } from "react-query";
import { Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button } from 'reactstrap';
import { useNavigate } from "react-router-dom";



const Movies = () =>{

    const { status, data: movies } = useQuery("movies", getMovies);
    
    const navigate = useNavigate();
    const goToMoviePage = () => {
        navigate("/movies/:movieId");
      };
    //console.log(movies)


    return(
        <div>
        <h1>Movies</h1>
        {status === "loading" ? (
            <Spinner>Loading...</Spinner>
            ) :  (movies?.map( ( { backdrop_path, title, overview})=> (
                        <Card>
                            <CardImg top width="100%" src={backdrop_path} alt="Card image cap" />                          
                            <CardBody>
                                <CardTitle>{title}</CardTitle>
                                <CardText>{overview}.</CardText>
                                <a href={goToMoviePage} class="btn btn-primary">See movie</a>
                            </CardBody>
                        </Card>
                        )
                    )
                )
        }          
    </div>  
    )
} 

export default Movies;
