import axios from "axios";

export const getMovies = async () => {
  const res = await axios.get("http://localhost:3001/movies");

  return res.data;
};

export const getMovie = async () => {
  const res = await axios.get("http://localhost:3001/movie/:id");

  return res.data;
};
