import { useParams } from "react-router-dom";
import { getMovie } from "../../api/movies";
import { useQuery } from "react-query";

const Movie = () => {
  const { movieId } = useParams();
  const { status, data} = useQuery(
    ["movie", movieId],
    () => getMovie(movieId)
  );

 console.log(data);

  return  (
    <div>
      <h1>Movie details</h1>
      <h2>{data.title}</h2>
      <h2>{data.tagline}</h2>
      <h4>Overview:</h4>
      <h2>{data.overview}</h2>
      <label>Stars:</label><h2>{data.vote_average}</h2>
      <label>IMDB:</label><a href={data.homepage} target="_blank">IMDB Link</a>
      <img src={data.poster_path}></img>
      
    </div>
  );
};

export default Movie;
